mapper.select:
SELECT COUNT(USER_ID)
FROM USER_TB
WHERE USER_ID = #{USER_ID}

SELECT USER_ID
FROM USER_TB
WHERE USER_NAME = #{USER_NAME}
AND USER_PHONE = #{USER_PHONE}

SELECT USER_ID, USER_PW
FROM USER_TB
WHERE USER_ID = #{USER_ID}
AND USER_NAME = #{USER_NAME}
AND USER_MAIL = #{USER_MAIL}
AND USER_PHONE = #{USER_PHONE}
